import React, { Component } from 'react';

class Alphabet extends Component {

    toSpeak = (id) => {
        var msg = new SpeechSynthesisUtterance(id);
        window.speechSynthesis.speak(msg);
    }
    displayElement = () => {
        switch (this.props.code) {
            case "orange":
                return (
                    <a onclick={this.toSpeak("one")}>
                        <img className="h1" src="./number/one.JPG" alt="" style={{ display: 'block', height: '100px' }} />
                    </a>
                )
            case "black":
                return (
                    <a onclick={this.toSpeak("six")}>
                        <img className="h2" src="./number/six.JPG" alt="" style={{ display: 'none' }} />
                    </a>
                )
            case "red":
                return (
                    <a onclick={this.toSpeak("five")}>
                        <img className="h3" src="./number/five.JPG" alt="" style={{ display: 'none' }} />
                    </a>
                )
            case "pink":
                return (
                    <a onclick={this.toSpeak("two")}>
                        <img className="h4" src="./number/two.JPG" alt="" style={{ display: 'none' }} />
                    </a>
                )
            case "brown":
                return (
                    <a onclick={this.toSpeak("three")}>
                        <img className="h5" src="./number/three.JPG" alt="" style={{ display: 'none' }} />
                    </a>
                )
            case "purple":
                return (
                    <a onClick={() => Gaudio.play()}>
                        <h1 className="tu5" style={{ marginRight: 100 }}>PURPLE</h1>
                    </a>
                )
            case "yellow":
                return (
                    <a onClick={() => Jaudio.play()}>
                        <h1 className="tu6" style={{ marginRight: 100 }}>YELLOW</h1>
                    </a>
                )
            case "blue":
                return (
                    <a onClick={() => Baudio.play()}>
                        <h1 className="tu8" style={{ marginRight: 100 }}>BLUE</h1>
                    </a>
                )
            case "green":
                return (
                    <a onClick={() => Caudio.play()}>
                        <h1 className="tu1" style={{ marginRight: 100 }}>GREEN</h1>
                    </a>
                )
            default:
                return (
                    <a onClick={() => Haudio.play()}>
                        <h1 className="tu7" style={{ marginRight: 100 }}>GRAY</h1>
                    </a>
                )
        }
    }

    render() {
        return (
            <div className="tu">
                {this.displayElement()}
            </div>
        );
    }
}

export default Alphabet;
