import React, {Component} from 'react';
import ImageList from "./ImageList";
import Alphabet from "./Alphabet";
import "./1.css"
class HomeEnglish extends Component {
    constructor(props) {
        super(props);
        this.state = {
            colors: ["orange", "black", "red", "pink", "brown", "", "vang", "xanhd", "xanhl", "xam"],
            color: ""

        }
    }
    toSpeak = (id) => {
        var msg = new SpeechSynthesisUtterance(id);
        window.speechSynthesis.speak(msg);
    }
    componentDidMount() {
        const presentColor = this.state.colors.find(color => color === this.props.match.params.color);
        if (presentColor) {
            this.setState({
                color: presentColor
            })
            this.toSpeak(presentColor)
        }
        else{
            this.props.history.push("/home-english/xanhl")
        }
    }

    render() {
        return (
            <div id="english">
                <div>
                    <h1 className="game-title">Play-Game</h1>
                </div>
                <div>
                    <h1 className="win" style={{display: 'none', marginTop: 350}}>WIN</h1>
                </div>
                <ImageList
                    code={this.state.color}
                />
                <Alphabet
                    code={this.state.color}
                />
            </div>
        );
    }
}

export default HomeEnglish;
