import React, { Component } from 'react';

class Alphabet extends Component {

    toSpeak = (id) => {
        var msg = new SpeechSynthesisUtterance(id);
        window.speechSynthesis.speak(msg);
    }
    displayElement = () => {
        switch (this.props.code) {
            case "orange":
                return (
                    <a onclick={this.toSpeak("one")}>
                        <img className="h1" src="./number/one.JPG" alt="" style={{ display: 'block', height: '100px' }} />
                    </a>
                )
            case "black":
                return (
                    <a onclick={this.toSpeak("six")}>
                        <img className="h2" src="./number/six.JPG" alt="" style={{ display: 'none' }} />
                    </a>
                )
            case "red":
                return (
                    <a onclick={this.toSpeak("five")}>
                        <img className="h3" src="./number/five.JPG" alt="" style={{ display: 'none' }} />
                    </a>
                )
            case "pink":
                return (
                    <a onclick={this.toSpeak("two")}>
                        <img className="h4" src="./number/two.JPG" alt="" style={{ display: 'none' }} />
                    </a>
                )
            case "brown":
                return (
                    <a onclick={this.toSpeak("three")}>
                        <img className="h5" src="./number/three.JPG" alt="" style={{ display: 'none' }} />
                    </a>
                )
            case "nine":
                return (
                    <a onclick={this.toSpeak("nine")}>
                        <img className="h6" src="./number/nine.JPG" alt="" style={{ display: 'none' }} />
                    </a>
                )
            case "four":
                return (
                    <a onclick={this.toSpeak("four")}>
                        <img className="h7" src="./number/four.JPG" alt="" style={{ display: 'none' }} />
                    </a>
                )
            case "seven":
                return (
                    <a onclick={this.toSpeak("seven")}>
                        <img className="h8" src="./number/seven.JPG" alt="" style={{ display: 'none' }} />
                    </a>
                )
            default:
                return (
                    <a onclick={this.toSpeak("eight")}>
                        <img className="h9" src="./number/eight.JPG" alt="" style={{ display: 'none' }} />
                    </a>
                )
        }
    }

    render() {
        return (
            <div className="tu">
                {this.displayElement()}
            </div>
        );
    }
}

export default Alphabet;
